"use strict";new Vue({el:".index_main",data:{list:[]},created:function(){this.getList()},methods:{getList:function(){var a=this;axios({method:"get",url:"https://derway.tw/admin/api/news"}).then(function(t){var e=0<t.data.data.length?t.data.data:[];a.list=[],e.forEach(function(n,t){2<t||a.list.push({id:n.id,year:new Date(n.date).getFullYear(),date:function(){function t(t){return t<10?"0"+t:t}var e=new Date(n.date).getMonth()+1,a=new Date(n.date).getDate();return t(e)+"/"+t(a)},title:n.title,href:"news_article.html?id="+n.id})})}).catch(function(){alert("最新消息列表取得失敗！")})}}});