"use strict";new Vue({el:".news_main",data:{article:{title:"",date:"",content:""}},created:function(){if(!this.getQueryParams().id)return alert("找不到該則消息！"),void(location.href="index.html");this.getList()},methods:{getQueryParams:function(t){var e=t||location.search;e=e.split("+").join(" ");for(var a,i={},n=/[?&]?([^=]+)=([^&]*)/g;a=n.exec(e);)i[decodeURIComponent(a[1])]=decodeURIComponent(a[2]);return i},getList:function(){var e=this;axios({method:"get",url:"https://derway.tw/admin/api/news/"+this.getQueryParams().id}).then(function(t){e.article=t.data.data}).catch(function(){alert("最新消息列表取得失敗！")})}}});